class Walker(object):
    def __init__(self, pad, ix, iy):
        self.x, self.y = ix, iy
        self.dirs = {"U": (0, -1), "D": (0, 1), "L": (-1, 0), "R": (1, 0)}
        self.pad = pad
        self.size = int(len(pad)**0.5)

    def walk_char(self, c):
        dx, dy = self.dirs[c]
        new_x, new_y = self.x + dx, self.y + dy
        if 0 <= new_x < self.size and 0 <= new_y < self.size:
            if self.pad[new_x+new_y*self.size] != "X":
                self.x = new_x
                self.y = new_y

    def get_num(self):
        return self.pad[self.x+self.size*self.y]

    def walk(self, inp):
        code = ""
        for s in inp.split('\n'):
            for c in s:
                self.walk_char(c)
            code += self.get_num()
        return code


